import React from 'react';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import { Container, SectionWrapper } from '@components/wrappers';
import { SimpleLink } from '@components/links';
import { Link } from 'react-router-dom';
import Button from '@components/Button';
import Img1 from '@assets/photos/slide1.webp';
import Img2 from '@assets/photos/slide2.webp';
import Img3 from '@assets/photos/slide3.webp';
import { useQuery } from '@styles/breakpoints';
import styled from 'styled-components';
import RedBgLine from '../../assets/photos/red-lines-bg.png';
import { desktop, mobile } from '@styles/breakpoints';

interface Props {
	active: boolean;
}

const CarouselStyles = styled(SectionWrapper)`
	background-image: url(${RedBgLine});
	background-position: center;
	background-repeat: no-repeat;
	background-size: 100% 100%;
	background-color: var(--lbg);
	@media ${mobile} {
		background-size: 100% 50%;
	}
	& > div {
		background-color: var(--subbg);
		border-radius: 20px;
	}
`;

interface SlideProp {
	img: string;
}

const Slide = styled.div<SlideProp>`
	text-align: left;
	@media ${desktop} {
		display: grid;
		grid-template-columns: repeat(2, minmax(0, 1fr));
		height: 700px;
		& > div:last-child {
			& > div {
				position: absolute;
				left: 0;
				bottom: 60px;
				padding: 20px;
				background: var(--lbg);
				max-width: 40%;
			}
			& > div > i {
				font-size: 20px;
				line-height: 30px;
			}
			& > div > p {
				font-style: italic;
				font-weight: 400;
				font-size: 16px;
				line-height: 24px;
				padding-top: 24px;
			}
		}
	}
	& > div:last-child {
		position: relative;
		background-image: url(${({ img }) => img});
		background-size: cover;
		background-position: center;
	}
	& > div:first-child {
		padding: 80px;
		h2 {
			margin-bottom: 20px;
			font-size: 48px;
			font-weight: 600;
		}
		button {
			margin-top: 80px;
		}
	}
	@media ${desktop} {
		display: flex;
		flex-direction: column-reverse;
		justify-content: start;
		height: 100%;
		& > div:first-child {
			padding: 0 20px 100px;
			h2 {
				font-size: 24px;
			}
			button {
				margin-top: 40px;
			}
		}
		& > div:last-child {
			height: 300px;
			& > div {
				display: none;
			}
		}
	}
`;

const SliderIndicator = styled.div<Props>`
	width: 0;
	height: 0;
	border-top: 10px solid transparent;
	border-bottom: 10px solid transparent;
	border-left: 10px solid var(--lbg);
	margin-right: 10px;
`;

const SliderPrev = styled.div<Props>`
	width: 0;
	height: 0;
	border-top: 10px solid transparent;
	border-bottom: 10px solid transparent;
	border-left: 10px solid var(--lbg);
`;

const SliderNext = styled.div<Props>`
	width: 0;
	height: 0;
	border-top: 10px solid transparent;
	border-bottom: 10px solid transparent;
	border-left: 10px solid var(--lbg);
`;

const CarouselComponent = () => {
	const { isDesktop } = useQuery();
	return (
		<CarouselStyles>
			<Container>
				<div>
					<Carousel
						renderIndicator={(clickHandler, isSelected) => <SliderIndicator onClick={clickHandler} active={isSelected} />}
						renderArrowPrev={(clickHandler, hasPrev) => isDesktop && <SliderPrev onClick={clickHandler} active={hasPrev} />}
						renderArrowNext={(clickHandler, hasNext) => isDesktop && <SliderNext onClick={clickHandler} active={hasNext} />}
						showArrows={isDesktop}
						preventMovementUntilSwipeScrollTolerance={true}
						swipeScrollTolerance={50}
						showStatus={false}
						showThumbs={false}
						showIndicators={!isDesktop}
						autoPlay
						infiniteLoop={!isDesktop}
						interval={7000}>
						<Slide img={Img1}>
							<div>
								<h2>Embrace Active Lifestyle</h2>
								<p>Walk your dog. Jog in the park. Hike a mountain. Stay active.</p>
								<br />
								<p>
									TipTop allows you to explore the physical world in a new dynamic way. We reward active lifestyle to help you form healthy habits and improve your physical and
									mental health.
								</p>
								<Link to='/links'>
									<Button variant='primary'>Join Our Community</Button>
								</Link>
							</div>
							<div>
								<div>
									<i>“Everywhere is within walking distance if you have the time.”</i>
									<p>– Steven Wright</p>
								</div>
							</div>
						</Slide>
						<Slide img={Img2}>
							<div>
								<h2>Earn Through Movement</h2>
								<p>All exercise should be profitable. Period.</p>
								<br />
								<p>
									TipTop transforms all physical activity into a financially rewarding journey. With us, you can make income simply through movement. Achieve your fitness and
									financial goals at the same time!
								</p>
								<Button variant='primary'>Download App</Button>
							</div>
							<div>
								<div>
									<i>“A journey of a thousand miles must begin with a single step.”</i>
									<p>– Lao Tzu</p>
								</div>
							</div>
						</Slide>
						<Slide img={Img3}>
							<div>
								<h2>Limit Your Carbon Footprint</h2>
								<p>We are committed to carbon neutrality in Web 3.0.</p>
								<br />
								<p>
									TipTop rewards movement to help us all reduce our environmental footprint. Our numerous partnerships and green initiatives with environmentally conscious
									companies demonstrate our commitment to a sustainable way of life.
								</p>
								<SimpleLink href='/contact'>
									<Button variant='primary'>Partnership Prospects</Button>
								</SimpleLink>
							</div>
							<div>
								<div>
									<i>“We cannot live on this planet as if we had another one to go to.”</i>
									<p> - Terri Swearingen</p>
								</div>
							</div>
						</Slide>
					</Carousel>
				</div>
			</Container>
		</CarouselStyles>
	);
};

export default CarouselComponent;
