import { useState, useRef } from 'react';
import RoadmapImg from '@assets/photos/roadmap-mob.webp';
import { Container, SectionWrapper } from '@components/wrappers';
import styled, { css } from 'styled-components';

interface Props {
	active: number;
}

const SectionBase = css`
	position: relative;
	padding: 160px 0 20px;
	h1 {
		margin-bottom: 20px;
	}
	@media (max-width: 1740px) {
		padding: 90px 0 20px;
	}
	@media (max-width: 1600px) {
		padding: 50px 0;
	}
`;
const BackgroundColor = css`
	&::before {
		content: '';
		position: absolute;
		top: 0;
		left: 0;
		width: 100%;
		height: 100%;
	}
`;
const RoadmapWrap = styled.section`
	background-image: url(${RoadmapImg});
	background-repeat: no-repeat;
	background-size: cover;
	${SectionBase};
	position: relative;
	color: var(--text);
	transition: all 0.5s;
	min-height: 710px;
	& > img {
		position: absolute;
		right: 0;
		top: 0;
		z-index: -1;
	}
	${BackgroundColor};
	&::before {
		z-index: -1;
		background: var(--special);
	}
	@media (max-width: 600px) {
		& > img {
			height: 50%;
		}
	}
`;
const YearWrap = styled.div<Props>`
	display: flex;
	align-items: center;
	margin-bottom: 40px;
	button {
		border: 0;
		background: none;
		border-radius: 0;
		padding: 10px 20px 10px 10px;
		font-weight: bold;
		font-size: 24px;
		cursor: pointer;
		opacity: 0.5;
		color: var(--text);
		&:hover {
			color: var(--text);
		}
		&:nth-child(${({ active }) => active}) {
			opacity: 1;
		}
	}
	@media (max-width: 1024px) {
		margin: 50px 0 80px;
	}
	@media (max-width: 600px) {
		margin: 40px 0;
	}
`;
const RoadmapContent = styled.div`
	overflow-x: scroll;
	&,
	ul {
		white-space: nowrap;
	}
	& > div {
		display: inline-block;
		vertical-align: top;
		width: 25%;
		padding-left: 20px;
		li {
			white-space: normal;
		}
	}
	span {
		display: block;
		font-weight: bold;
		margin-bottom: 5px;
	}
	// Hide scrollbar
	::-webkit-scrollbar {
		display: none;
	}
	-ms-overflow-style: none; /* IE and Edge */
	scrollbar-width: none;
	@media (max-width: 600px) {
		& > div {
			display: inline-block;
			vertical-align: top;
			width: 100%;
			padding: 0;
			ul {
				padding-left: 20px;
			}
			& > div + div {
				margin-top: 20px;
			}
		}
	}
`;

const Years = [2022, 2023, 2024];
export const RoadmapData = [
	{
		q: 4,
		content: [
			'App Launch',
			'Conference Attendances Worldwide',
			'Marketplace Release',
			'Marketing Kickoff',
			'Referral Program Implementation',
			'TIP Token Issuance',
		],
		year: 2022,
	},
	{
		q: 1,
		content: [
			'Community Building Boost',
			'Representatives Program Release',
			'Issuance of TOP Token',
			'Exchange Listings TOP Token',
			'In-app Token Swap Release',
		],
		year: 2023,
	},
	{
		q: 2,
		content: [
			'Governance Poll Introduction',
			'Integrations with 3rd Party Vendors',
			'Addition of Multiple Languages',
			'Extensive Marketing Campaign',
			'Staking Solution for TIP & TOP Tokens',
		],
		year: 2023,
	},
	{
		q: 3,
		content: [
			'Feature: Sneaker Renting',
			'User Data Analytics Integration',
			'Collaboration with Fashion Brands',
			'Feature: Minting of Special & Themed Collections',
		],
		year: 2023,
	},
	{
		q: 4,
		content: [
			'Feature: Personalized Item Minting',
			'Events: Remote Challenges',
			'Feature: Leaderboards and prizes',
			'Feature: In-App Ad Placements',
		],
		year: 2023,
	},
	{
		q: 1,
		content: ['Feature: Online / Offline Competitions', 'Virtual Events with Brands and Conferences'],
		year: 2024,
	},
	{
		q: 2,
		content: ['Ambassador Program Launch', 'Ambassador Hosted Events'],
		year: 2024,
	},
	{
		q: 3,
		content: ['Wearables Gadgets'],
		year: 2024,
	},
	{
		q: 4,
		content: ['Wearable Digital Shoes'],
		year: 2024,
	},
];

const RoadmapMob = () => {
	const [year, setYear] = useState<number | undefined>(1);
	const scrollRef = useRef<HTMLDivElement>(null);

	const handleYearChange = (yr: number | undefined): void => {
		if (yr !== year) {
			const scrollWidth = scrollRef.current?.offsetWidth;
			const toScroll = window.innerWidth > 600 ? (yr === 2 ? scrollWidth / 4 : scrollWidth) : scrollWidth;
			if (scrollRef.current) {
				scrollRef.current.scrollLeft = (yr > year ? toScroll : -toScroll) * year;
			}
			setYear(yr);
		}
	};

	return (
		<RoadmapWrap id='roadmap'>
			<Container>
				<h2>Roadmap</h2>
				<YearWrap active={year}>
					{Years.map((i, idx) => (
						<button key={i} onClick={() => handleYearChange(idx + 1)}>
							{i}
						</button>
					))}
				</YearWrap>
				{window.innerWidth > 600 ? (
					<RoadmapContent ref={scrollRef}>
						{RoadmapData.map((i, idx) => (
							<div key={idx}>
								<span>Q{i.q}</span>
								<ul key={idx}>
									{i.content.map((item) => (
										<li key={item}>{t(item)}</li>
									))}
								</ul>
							</div>
						))}
					</RoadmapContent>
				) : (
					<RoadmapContent ref={scrollRef}>
						<div>
							{RoadmapData.slice(0, 1).map((i, idx) => (
								<div key={idx}>
									<span>Q{i.q}</span>
									<ul key={idx}>
										{i.content.map((item) => (
											<li key={item}>{t(item)}</li>
										))}
									</ul>
								</div>
							))}
						</div>
						<div>
							{RoadmapData.slice(1, 5).map((i, idx) => (
								<div key={idx}>
									<span>Q{i.q}</span>
									<ul key={idx}>
										{i.content.map((item) => (
											<li key={item}>{t(item)}</li>
										))}
									</ul>
								</div>
							))}
						</div>
						<div>
							{RoadmapData.slice(5, 9).map((i, idx) => (
								<div key={idx}>
									<span>Q{i.q}</span>
									<ul key={idx}>
										{i.content.map((item) => (
											<li key={item}>{t(item)}</li>
										))}
									</ul>
								</div>
							))}
						</div>
					</RoadmapContent>
				)}
			</Container>
		</RoadmapWrap>
	);
};
export default RoadmapMob;
