import React from 'react';
import styled from 'styled-components';
import { SectionWrapper, Container, Box, GridWrapper, FlexWrapper, TextBase } from '@components/wrappers';
import Button from '@components/Button';
import AnimatedVideo from '@components/AnimatedVideo';
import PhonesVideo from '../../assets/videos/phones-section1.webm';
import RedBgLine from '../../assets/photos/red-lines-bg.png';
import ContainerBg from '../../assets/photos/container-bg.jpg';
import { ReactComponent as Icon1 } from '../../assets/svg/every-step1.svg';
import { ReactComponent as Icon2 } from '../../assets/svg/every-step2.svg';
import { ReactComponent as Icon3 } from '../../assets/svg/every-step3.svg';
import { ReactComponent as Icon4 } from '../../assets/svg/every-step4.svg';

const EveryStepStyles = styled(SectionWrapper)`
	background-image: url(${RedBgLine});
	background-position: center;
	background-repeat: no-repeat;
	background-size: 100% 100%;
	& > div {
		background-image: url(${ContainerBg});
		background-position: center;
		background-repeat: no-repeat;
		background-size: 100% 100%;
		border-radius: 20px;
		margin-top: 20px;
	}
`;

const Boxes = [
	{
		img: <Icon1 />,
		text: 'Active Lifestyle',
	},
	{
		img: <Icon2 />,
		text: 'Rewarding Ecosystem',
	},
	{
		img: <Icon3 />,
		text: 'Unique Collectibles',
	},
	{
		img: <Icon4 />,
		text: 'Sustainable Choice',
	},
];

const EveryStep: React.FC = () => {
	return (
		<EveryStepStyles>
			<div>
				<Container>
					<GridWrapper col={2}>
						<div>
							<h1>Every Step Counts</h1>
							<p>Get in Tip-Top shape and earn real rewards through movement within a self-sustaining ecosystem.</p>
							<FlexWrapper>
								<Button variant={'primary'}>Download App</Button>
							</FlexWrapper>
						</div>
						<AnimatedVideo width='100%' height='600px' srcSafari={'https://tiptop-media.s3.eu-central-1.amazonaws.com/mp4/phone-safari.mov'} srcChrome={PhonesVideo} />
						<GridWrapper>
							{Boxes.map(({ img, text }) => (
								<Box>
									<Box>{img}</Box>
									<TextBase>{tex}</TextBase>
								</Box>
							))}
						</GridWrapper>
					</GridWrapper>
				</Container>
			</div>
		</EveryStepStyles>
	);
};

export default EveryStep;
