import React, { useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
const lightning = keyframes`
  0% {
    opacity: 1;
    transform: scale(1);
  }
  25% {
    opacity: 0.5;
    transform: scale(1.5) translate(5px, -5px);
  }
  50% {
    opacity: 1;
    transform: scale(0.8) translate(-10px, 10px);
  }
  75% {
    opacity: 0.5;
    transform: scale(1.2) translate(-5px, -5px);
  }
  100% {
    opacity: 1;
    transform: scale(1) translate(0, 0);
  }
`;

const Lightning = styled.div`
	position: absolute;
	width: 3px;
	height: 20px;
	background: white;
	animation: ${lightning} 0.5s linear infinite;
	transform-origin: bottom center;
`;

const Blob = styled.div`
	position: relative;
	width: 200px;
	height: 200px;
	background: linear-gradient(to right, aquamarine, mediumpurple);
`;

const MouseMoveAnimation: React.FC = () => {
	const blobRef = useRef<HTMLDivElement>(null);

	useEffect(() => {
		const blob = blobRef.current;

		const onMouseMove = (event: MouseEvent) => {
			const { clientX, clientY } = event;
			const scrollY = window.pageYOffset;
			blob?.animate(
				{
					left: `${clientX}px`,
					top: `${clientY + scrollY}px`,
				},
				{ duration: 1500, fill: 'forwards' },
			);
		};
		window.addEventListener('mousemove', onMouseMove);

		return () => {
			window.removeEventListener('mousemove', onMouseMove);
		};
	}, []);

	return <Blob ref={blobRef} />;
};

export default MouseMoveAnimation;
