import React from 'react';
import styled from 'styled-components';
import RoadmapImg from '@assets/photos/roadmap.webp';
import { ReactComponent as RedLine } from '@assets/svg/roadmap-red-line.svg';

const RoadmapStyles = styled.section`
	position: relative;
	width: 100%;
	height: 2163px;
	background-image: url(${RoadmapImg});
	background-position: center 0;
	background-repeat: no-repeat;
	background-size: auto 100%;
	h3 {
		margin: 40px 60px;
		width: 350px;
	}
`;

const RoadMapRedLine = styled(RedLine)`
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-60.8%, -50%);
	margin-top: 141px;
	path {
		filter: drop-shadow(0 0 15px red);
		animation: glow 1s ease-in-out infinite alternate;
	}
	@keyframes glow {
		from {
			opacity: 0.4;
		}
		to {
			opacity: 1;
		}
	}
`;

const Roadmap: React.FC = () => {
	return (
		<RoadmapStyles>
			<RoadMapRedLine />
		</RoadmapStyles>
	);
};

export default Roadmap;
