import React, { useState, useRef } from 'react';
import RoadmapImg from '@assets/photos/roadmap-mob.webp';
import { Container, SectionWrapper, Box, FlexWrapper } from '@components/wrappers';
import styled, { css } from 'styled-components';
import { RoadmapData } from './constants';

interface Props {
	active: number;
}

const RoadmapWrap = styled.section<Props>`
	height: 1000px;
	background-image: url(${RoadmapImg});
	background-repeat: no-repeat;
	background-size: cover;
	position: relative;
	& > div > div:last-of-type > div {
		position: absolute;
		overflow: hidden;
		transition: all 3s;
		:nth-child(1) {
			top: 190px;
			left: 0;
		}
		:nth-child(2) {
			top: 120px;
			left: -200%;
		}
		:nth-child(3) {
			top: 120px;
			left: -200%;
		}
	}
`;

const RoadmapCard = styled(FlexWrapper)`
	margin: 20px;
	background-color: rgba(0, 0, 0, 0.4);
	padding: 20px;
	border-radius: 8px;
	* {
		margin: 5px;
	}
	span {
		font-weight: 600;
	}
`;

const ButtonStyles = styled(Box)<Props>`
	button {
		border: 0;
		background: none;
		border-radius: 0;
		padding: 10px 20px 10px 10px;
		font-weight: bold;
		font-size: 24px;
		cursor: pointer;
		opacity: ${({ active }) => (active ? '1' : '0.5')};
		color: var(--text);
	}
`;

const RoadmapMob: React.FC = () => {
	const [active, setActive] = useState<number>(1);
	return (
		<RoadmapWrap id='roadmap' active={active}>
			<Container>
				<h2>Roadmap</h2>
				<ButtonStyles active={active}>
					<button>2022</button>
					<button>2023</button>
					<button>2024</button>
				</ButtonStyles>
				<Box>
					<RoadmapCard>
						{RoadmapData.slice(0, 1).map(({ q, content }, i) => (
							<div key={i}>
								<span>Q{q}</span>
								<ul>
									{content.map((item) => (
										<li key={item}>{item}</li>
									))}
								</ul>
							</div>
						))}
					</RoadmapCard>
					<RoadmapCard>
						{RoadmapData.slice(1, 5).map(({ q, content }, i) => (
							<div key={i}>
								<span>Q{q}</span>
								<ul>
									{content.map((item) => (
										<li key={item}>{item}</li>
									))}
								</ul>
							</div>
						))}
					</RoadmapCard>
					<RoadmapCard>
						{RoadmapData.slice(5, 9).map(({ q, content }, i) => (
							<div key={i}>
								<span>Q{q}</span>
								<ul>
									{content.map((item) => (
										<li key={item}>{item}</li>
									))}
								</ul>
							</div>
						))}
					</RoadmapCard>
				</Box>
			</Container>
		</RoadmapWrap>
	);
};
export default RoadmapMob;
