import React from 'react';
import { SectionWrapper, Container, Box, GridWrapper, FlexWrapper, TextBase } from '@components/wrappers';
import styled from 'styled-components';
import AnimatedVideo from '@components/AnimatedVideo';
import TipTopApp from '@assets/videos/3d-phone-chrome.webm';

const InfoStyles = styled(GridWrapper)`
	span {
		font-size: 24px;
		font-weight: 700;
		display: flex;
		align-items: center;
		justify-content: center;
		background: rgba(0, 0, 0, 0.4);
		height: 60px;
		width: 60px;
		border-radius: 100%;
	}
	h6 {
		font-size: 18px;
	}
`;

const JoinTipTopData = [
	{
		span: 1,
		h6: 'Download the TipTop App',
		p: 'You can find the app on Google Play or Apple Play Store',
	},
	{
		span: 2,
		h6: 'Register an Account',
		p: 'By registering, you agree with our Terms and Privacy Policy',
	},
	{
		span: 3,
		h6: 'Move to Earn',
		p: 'Put on the Premium Sneaker and give TipTop a try!',
	},
];

const JoinTipTop: React.FC = () => {
	return (
		<SectionWrapper backgroundColor='var(--lbg)'>
			<Container>
				<GridWrapper col={2}>
					<Box>
						<AnimatedVideo srcChrome={TipTopApp} srcSafari='https://tiptop-media.s3.eu-central-1.amazonaws.com/mp4/3d-phone-safari.mov' />
					</Box>
					<Box>
						<h3>Join TipTop in 3 easy Steps</h3>
						{JoinTipTopData.map(({ span, h6, p }) => (
							<InfoStyles colExact='60px auto'>
								<span>{span}</span>
								<Box>
									<h6>{h6}</h6>
									<TextBase>{p}</TextBase>
								</Box>
							</InfoStyles>
						))}
						<FlexWrapper gap='20px'>
							{/* <SimpleLink href='https://play.google.com/store/apps/details?id=com.tiptop.io'>
								<GoogleImg width='150px' />
							</SimpleLink>
							<SimpleLink href='https://tiptop.io/app.apk'>
								<AppInstaller width='150px' />
							</SimpleLink> */}
							{/* <SimpleLink href="https://apple.com">
                                <AppleImg />
                            </SimpleLink> */}
						</FlexWrapper>
					</Box>
				</GridWrapper>
			</Container>
		</SectionWrapper>
	);
};

export default JoinTipTop;
