import React from 'react';
import styled from 'styled-components';
import { SectionWrapper, Container, Box, GridWrapper, FlexWrapper } from '@components/wrappers';
import Button from '@components/Button';
import AnimatedVideo from '@components/AnimatedVideo';
import PhonesVideo from '../../assets/videos/phones-section1.webm';
import RedBgLine from '../../assets/photos/red-lines-bg.png';
import ContainerBg from '../../assets/photos/container-bg.jpg';

const EveryStepStyles = styled(SectionWrapper)`
	background-image: url(${RedBgLine});
	background-position: center;
	background-repeat: no-repeat;
	background-size: 100% 100%;
	& > div {
		background-image: url(${ContainerBg});
		background-position: center;
		background-repeat: no-repeat;
		background-size: 100% 100%;
		border-radius: 20px;
	}
`;

const EveryStep: React.FC = () => {
	return (
		<EveryStepStyles>
			<div>
				<Container>
					<GridWrapper col={2}>
						<div>
							<h1>Every Step Counts</h1>
							<p>Get in Tip-Top shape and earn real rewards through movement within a self-sustaining ecosystem.</p>
							<FlexWrapper>
								<Button variant={'primary'}>Download App</Button>
							</FlexWrapper>
						</div>
						<AnimatedVideo width='100%' height='600px' srcSafari={'https://tiptop-media.s3.eu-central-1.amazonaws.com/mp4/phone-safari.mov'} srcChrome={PhonesVideo} />
					</GridWrapper>
				</Container>
			</div>
		</EveryStepStyles>
	);
};

export default EveryStep;
