import React, { useState, useRef } from 'react';
import RoadmapImg from '@assets/photos/roadmap-mob.webp';
import { Container, SectionWrapper, Box, FlexWrapper, GridWrapper } from '@components/wrappers';
import styled, { css } from 'styled-components';
import { RoadmapData } from './constants';

interface Props {
	active: number;
}

const RoadmapCard = styled(GridWrapper)`
	padding: 20px;
	* {
		margin: 5px;
	}
	span {
		font-weight: 600;
	}
	& > div {
		overflow: hidden;
		margin: 20px;
		background-color: rgba(0, 0, 0, 0.4);
		padding: 20px 30px;
		border-radius: 8px;
		font-weight: 600;
		min-width: 360px;
	}
	position: absolute;
	overflow: hidden;
	transition: all 2s linear;
	:nth-child(1) {
		top: 190px;
		left: ${({ active }) => (active === 1 ? '0' : active === 2 ? '-100%' : '-200%')};
	}
	:nth-child(2) {
		top: 190px;
		left: ${({ active }) => (active === 2 ? '0' : active === 3 ? '-100%' : '100%')};
	}
	:nth-child(3) {
		top: 190px;
		left: ${({ active }) => (active === 3 ? '0' : active === 2 ? '100%' : '200%')};
	}
`;

const RoadmapWrap = styled.section`
	height: 1000px;
	background-image: url(${RoadmapImg});
	background-repeat: no-repeat;
	background-size: cover;
	position: relative;
`;

const ButtonStyles = styled(Box)<Props>`
	button {
		border: 0;
		background: none;
		border-radius: 0;
		padding: 10px 20px 10px 10px;
		font-weight: bold;
		font-size: 24px;
		cursor: pointer;
		opacity: 0.5;
		color: var(--text);
		:nth-child(${({ active }) => active}) {
			opacity: 1;
		}
	}
`;

const RoadmapMob: React.FC = () => {
	const [active, setActive] = useState<number>(1);
	return (
		<RoadmapWrap id='roadmap'>
			<Container>
				<h2>Roadmap</h2>
				<ButtonStyles active={active}>
					<button onClick={() => setActive(1)}>2022</button>
					<button onClick={() => setActive(2)}>2023</button>
					<button onClick={() => setActive(3)}>2024</button>
				</ButtonStyles>
				<Box>
					<RoadmapCard colXl={2} active={active}>
						{RoadmapData.slice(0, 1).map(({ q, content }, i) => (
							<Box key={i}>
								<span>Q{q}</span>
								<ul>
									{content.map((item) => (
										<li key={item}>{item}</li>
									))}
								</ul>
							</Box>
						))}
					</RoadmapCard>
					<RoadmapCard colXl={2} active={active}>
						{RoadmapData.slice(1, 5).map(({ q, content }, i) => (
							<div key={i}>
								<span>Q{q}</span>
								<ul>
									{content.map((item) => (
										<li key={item}>{item}</li>
									))}
								</ul>
							</div>
						))}
					</RoadmapCard>
					<RoadmapCard colXl={2} active={active}>
						{RoadmapData.slice(5, 9).map(({ q, content }, i) => (
							<div key={i}>
								<span>Q{q}</span>
								<ul>
									{content.map((item) => (
										<li key={item}>{item}</li>
									))}
								</ul>
							</div>
						))}
					</RoadmapCard>
				</Box>
			</Container>
		</RoadmapWrap>
	);
};
export default RoadmapMob;
