import React, { useState, useRef } from 'react';
import RoadmapImg from '@assets/photos/roadmap-mob.webp';
import { Container, SectionWrapper, Box, FlexWrapper } from '@components/wrappers';
import styled, { css } from 'styled-components';

interface Props {
	active: number;
}

interface RoadmapDataItem {
	q: number;
	content: string[];
	year: number;
}

const RoadmapWrap = styled.section`
	height: 1000px;
	background-image: url(${RoadmapImg});
	background-repeat: no-repeat;
	background-size: cover;
	position: relative;
	& > div > div:last-of-type {
		position: absolute;
		overflow: hidden;
		transition: all 3s;
		:nth-child(1) {
			top: 120px;
			left: 10px;
		}
		:nth-child(3) {
			top: 120px;
			left: -200%;
		}
		:nth-child(4) {
			top: 120px;
			left: -200%;
		}
	}
`;

const RoadmapCard = styled(FlexWrapper)`
	background-color: rgba(0, 0, 0, 0.4);
	padding: 20px;
	border-radius: 8px;
	* {
		margin: 5px;
	}
	span {
		font-weight: 600;
	}
`;

export const RoadmapData: RoadmapDataItem[] = [
	{
		q: 4,
		content: [
			'App Launch',
			'Conference Attendances Worldwide',
			'Marketplace Release',
			'Marketing Kickoff',
			'Referral Program Implementation',
			'TIP Token Issuance',
		],
		year: 2022,
	},
	{
		q: 1,
		content: [
			'Community Building Boost',
			'Representatives Program Release',
			'Issuance of TOP Token',
			'Exchange Listings TOP Token',
			'In-app Token Swap Release',
		],
		year: 2023,
	},
	{
		q: 2,
		content: [
			'Governance Poll Introduction',
			'Integrations with 3rd Party Vendors',
			'Addition of Multiple Languages',
			'Extensive Marketing Campaign',
			'Staking Solution for TIP & TOP Tokens',
		],
		year: 2023,
	},
	{
		q: 3,
		content: [
			'Feature: Sneaker Renting',
			'User Data Analytics Integration',
			'Collaboration with Fashion Brands',
			'Feature: Minting of Special & Themed Collections',
		],
		year: 2023,
	},
	{
		q: 4,
		content: [
			'Feature: Personalized Item Minting',
			'Events: Remote Challenges',
			'Feature: Leaderboards and prizes',
			'Feature: In-App Ad Placements',
		],
		year: 2023,
	},
	{
		q: 1,
		content: ['Feature: Online / Offline Competitions', 'Virtual Events with Brands and Conferences'],
		year: 2024,
	},
	{
		q: 2,
		content: ['Ambassador Program Launch', 'Ambassador Hosted Events'],
		year: 2024,
	},
	{
		q: 3,
		content: ['Wearables Gadgets'],
		year: 2024,
	},
	{
		q: 4,
		content: ['Wearable Digital Shoes'],
		year: 2024,
	},
];
const Years: number[] = [2022, 2023, 2024];
const RoadmapMob: React.FC = () => {
	return (
		<RoadmapWrap id='roadmap'>
			<Container>
				<h2>Roadmap</h2>
				<Box></Box>
				<Box>
					<RoadmapCard>
						{RoadmapData.slice(0, 1).map(({ q, content }, i) => (
							<div key={i}>
								<span>Q{q}</span>
								<ul>
									{content.map((item) => (
										<li key={item}>{item}</li>
									))}
								</ul>
							</div>
						))}
					</RoadmapCard>
					<RoadmapCard>
						{RoadmapData.slice(1, 5).map(({ q, content }, i) => (
							<div key={i}>
								<span>Q{q}</span>
								<ul>
									{content.map((item) => (
										<li key={item}>{item}</li>
									))}
								</ul>
							</div>
						))}
					</RoadmapCard>
					<RoadmapCard>
						{RoadmapData.slice(5, 9).map(({ q, content }, i) => (
							<div key={i}>
								<span>Q{q}</span>
								<ul>
									{content.map((item) => (
										<li key={item}>{item}</li>
									))}
								</ul>
							</div>
						))}
					</RoadmapCard>
				</Box>
			</Container>
		</RoadmapWrap>
	);
};
export default RoadmapMob;
